mapper.update:
create or replace trigger  TRG_C_LOGIN_RESPONSIBLE
    before insert on  C_LOGIN_RESPONSIBLE
    for each row
    begin
        select SEQ_C_LOGIN_RESPONSIBLE.nextval into :new.id from dual;
    end TRG_C_LOGIN_RESPONSIBLE;

